package pkg_animal;

public class Lion extends Herivore{

	@Override
	public void eat() {
		// TODO Auto-generated method stub
		System.out.println("사자가 고기를 뜯어먹는다.");
	}

	//오버 라이딩
	//부모클래스의 메소드 이름을 똑같이 작성한다.
	//리턴형, 메소드명, 매개변수가 완벽히 똑같아야 한다.
	
	//부모가 물려주고 있는 eat() 메소드를 그대로 사용 하지 않고
	//사자가 고기를 먹는다는 형태로 다시 정의 했다.
	
	//부모의 멧드를 손쉽게 오버라이딩 하는 방법
	//마우스 오른쪽 -> source -> override/implement Methods -> 오버라이딩 할 메소드 선택
	
	//@Override : 이노테이션
	//컴파일러에게 오버라이드된 메소드다 라는 말을 알려준다.
	//오버라이드가 안된 메소드인데 @override가 붙으면 애러발생
}
